# Funktion zum Extrahieren der erforderlichen Informationen
def extract_info(text):
    try:
        # Extrahiert den JSON-Teil aus dem Textblock
        json_str = text.split(' = ')[1].split(';')[0].strip()
        data = json.loads(json_str)
        return data
    except Exception as e:
        print(f"Fehler beim Parsen des JSON: {e}")
        return []

# Funktion zum Herunterladen und Speichern der Bilder
def download_and_save_images(data, directory):
    count = 0
    for entry in data:
        firstname = entry.get('firstname', '')
        lastname = entry.get('lastname', '')
        img_url = entry.get('img', '')
        img_funny_url = entry.get('imgFunny', '')

        folder_name = f"{lastname}-{firstname}"
        folder_path = os.path.join(directory, folder_name)
        os.makedirs(folder_path, exist_ok=True)

        # Download und Speicherung jedes Bildes
        for url in [img_url, img_funny_url]:
            if url:
                try:
                    response = requests.get(url)
                    if response.status_code == 200:
                        file_path = os.path.join(folder_path, url.split('/')[-1])
                        with open(file_path, 'wb') as file:
                            file.write(response.content)
                            count += 1
                except Exception as e:
                    print(f"Fehler beim Herunterladen von {url}: {e}")
    return count

# Hauptfunktion
def main(textblock):
    data = extract_info(textblock)

    # Tkinter GUI zur Ordnerauswahl
    root = Tk()
    root.withdraw()  # Versteckt das Hauptfenster von Tk
    directory = filedialog.askdirectory()  # Öffnet den Dialog zur Ordnerauswahl

    if not directory:
        print("Kein Ordner ausgewählt. Abbruch.")
        return

    # Bilder herunterladen und speichern
    count = download_and_save_images(data, directory)
    print(f"{count} Bilder wurden heruntergeladen und gespeichert.")

# Textblock einlesen und Hauptfunktion ausführen
if __name__ == "__main__":
    main(textblock)
